import React from 'react';
import '../../../styles/main/__cards/main__cards.css';
import '../../../styles/main/__cards/_middle/main__cards_middle.css';

function Cards({paymentMode, title, montlhyPrice, annualyPrice, storage, users, sendGb, itsMiddle}) {
    return (
        <div className='cards__container'>
            <div className={`cards__card ${itsMiddle ? 'cards__card_middle-card' : ''}`}>
                <h3 className={`cards__card-title  ${itsMiddle ? 'cards__card-title_middle-card' : ''}`}>{title}</h3>
                <h2 className={`cards__card-price  ${itsMiddle ? 'cards__card-price_middle-card' : ''}`}>{paymentMode ? montlhyPrice : annualyPrice}</h2>
                <p className={`cards__card-feuture ${itsMiddle ? 'cards__card-feuture_middle-card' : ''}`}>{storage} Storage</p>
                <p className={`cards__card-feuture ${itsMiddle ? 'cards__card-feuture_middle-card' : ''}`}>{users} Users Allowed</p>
                <p className={`cards__card-feuture cards__card-feuture_last ${itsMiddle ? 'cards__card-feuture_middle-card cards__card-feuture_last_middle-card' : ''}`}>Send up to {sendGb}</p>
                <a className={`cards__card-link ${itsMiddle ? 'cards__card-link_middle-card' : ''}`} href='www.linkedin.com/in/aldo-navarro-147b03282' target='_BLANK'>LEARN MORE</a>
            </div>
        </div>
    );
}

export default Cards;
