import React from 'react';
import '../../../styles/main/__cards/main__cards.css';
import '../../../styles/main/__cards/_middle/main__cards_middle.css';
import Card from './__card/cards__card.js';

const storageDeals = [
    {
        title: "Basic",
        montlhyPrice: "19.99",
        annualyPrice: "149.00",
        storage: "500 GB",
        users: "2",
        sendGb: "3 GB",
        itsMiddle: false,
    },
    {
        title: "Professional",
        montlhyPrice: "24.99",
        annualyPrice: "199.00",
        storage: "1 TB",
        users: "5",
        sendGb: "10 GB",
        itsMiddle: true,
    },
    {
        title: "Basic",
        montlhyPrice: "29.99",
        annualyPrice: "249.00",
        storage: "2 TB",
        users: "10",
        sendGb: "20 GB",
        itsMiddle: false,
    }
]

function Cards({paymentMode}) {
    return (
        <div className='cards__container'>
            {storageDeals.map((item, index) => (
            <Card key={index} title={item.title} paymentMode={paymentMode} montlhyPrice={item.montlhyPrice} annualyPrice={item.annualyPrice} storage={item.storage} users={item.users} sendGb={item.sendGb} itsMiddle={item.itsMiddle}/>
            ))}
        </div>
    );
}

export default Cards;
