import React from 'react';
import { useState, useEffect } from 'react';
import '../../styles/main/main.css';
import PaymentType from './__paymentType/main__paymentType.js';
import Cards from './__cards/main__cards.js';

function Main() {

    const [paymentMode, setPaymentMode] = useState(true);
    useEffect(() => {
        const mostrarAlerta = () => {
          setTimeout(() => {
            alert("Hola");
          }, 3000); // 3000 milisegundos (3 segundos) de espera antes de mostrar la alerta
        };

        mostrarAlerta();
      }, []);

    return (
        <div className="main">
            <div className='deal__overlay'></div>
            <div className='deal__container'>
                <h3 className='deal__title'>"Seize the annual payment offer and get more for less!"</h3>
            </div>
            <PaymentType paymentMode={paymentMode} setPaymentMode={setPaymentMode}/>
            <Cards paymentMode={paymentMode}/>
        </div>
    );
}

export default Main;
